    document.addEventListener('DOMContentLoaded', function() {
        const filterSelect = document.getElementById('filter');
        const table = document.getElementById('student-table');
        const rows = table.getElementsByTagName('tr');
        const headerRow = table.querySelector('thead tr');

        // Check if there is a previously selected value in localStorage
        const savedValue = localStorage.getItem('selectedValue');
        if (savedValue) {
            filterSelect.value = savedValue; // Set the value of the select element
        }

        filterSelect.addEventListener('change', function() {
            const selectedValue = filterSelect.value;

            // Save the selected value to localStorage
            localStorage.setItem('selectedValue', selectedValue);

            // Iterate over the table rows and hide/show based on the filter criteria
            for (let i = 0; i < rows.length; i++) {
                const row = rows[i];

                if (row === headerRow) {
                    // Skip the header row
                    continue;
                }

                const checkboxes = row.querySelectorAll('.getid');
                let isChecked = true; // Flag to track if any checkbox is checked

                for (let j = 0; j < checkboxes.length; j++) {
                    const checkbox = checkboxes[j];

                    if (!checkbox.checked) {
                        isChecked = false;
                        break; // Exit the inner loop if any checkbox is unchecked
                    }
                }

                if (selectedValue === '' || (selectedValue === '0' && !isChecked) || (selectedValue ===
                        '1' && isChecked)) {
                    // Show rows that match the filter criteria
                    row.style.display = 'table-row';
                } else {
                    // Hide rows that don't match the filter criteria
                    row.style.display = 'none';
                }
            }
        });

        // Trigger the change event on page load to apply the filter
        filterSelect.dispatchEvent(new Event('change'));
    });
    // Set the scroll position of the table container on page load
    $(document).ready(function() {
        var tableContainer = $('.overflow-x-auto');
        tableContainer.scrollLeft(tableContainer[0].scrollWidth);
    });
