<script>
    $('.input-text').keydown(function(event) {
        var inputText = $(this);
        inputText.css('color', 'red');

        if (event.keyCode === 13 && inputText.val() != "") {
            event.preventDefault();

            var id = inputText.data('student');
            var department = inputText.data('department');
            var remark = inputText.val();
            var rowIndex = inputText.closest('tr').index();
            var targetButton = $('table tbody tr:eq(' + rowIndex + ')').find('.getid');
            targetButton.prop('disabled', false).prop('checked', false);
            inputText.css('color', 'black');

            var url = "/student-clearance?student=" + id + '&department=' + department + '&remarks=' + remark +
                '&semester=' + inputText.data('semester');

            $.get(url)
                .done(function(data) {
                    console.log(data);
                    // Handle the response data as needed
                })
                .fail(function() {
                    console.log('Failed to perform student clearance.');
                    // Handle the failure scenario
                })
                .always(function() {
                    location.reload();
                    // refreshTable();
                });
        }
    });

    $('.getid').on('click', function() {
        var id = $(this).data('id');
        var rowIndex = $(this).closest('tr').index();
        var targetButton = $('table tbody tr:eq(' + rowIndex + ')').find('.getid');
        var targetInput = $('table tbody tr:eq(' + rowIndex + ')').find('.input-text');

        if (id) {
            if ($(this).is(':checked')) {
                targetButton.prop('disabled', true);
                targetInput.val('');
                console.log(rowIndex);

                var url = "/student-clearance-update?id=" + encodeURIComponent(id);
                $.get(url, function(data) {
                    console.log("Request successful");
                });
            } else {
                targetButton.prop('disabled', false);
            }
        } else {
            // Handle the scenario where the ID is not available
            console.log("No ID available for the data.");
        }
    });

    // Set the scroll position of the table container on page load
    $(document).ready(function() {
        var tableContainer = $('.overflow-x-auto');
        tableContainer.scrollLeft(tableContainer[0].scrollWidth);
    });
</script>
